def insert(node, data): 
    # אם העץ ריק, החזר צומת חדש
    if node is None: 
        return Node(data) 

    # אחרת, חפש במקום הנכון בעץ
    if data < node.data: 
        node.left = insert(node.left, data) 
    else: 
        node.right = insert(node.right, data) 

    # החזר את מצביע הצומת (שלא השתנה)
    return node 

# פונקציה לחיפוש נתון מסוים 
def search(node, data): 

    # מקרה בסיסי: אם השורש הוא None או אם המפתח נמצא בשורש
    if node is None or node.data == data: 
        return node 

    # המפתח גדול ממפתח השורש
    if node.data < data: 
        return search(node.right, data) 

    # המפתח קטן ממפתח השורש
    return search(node.left, data)

